package com.syntax.class02;

public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 15;
		num = 10;
		System.out.println(num);

		int number = 10;
		number += 2;
		System.out.println(number);

		number++;
		System.out.println(number);

		number++;
		System.out.println(number);
		number = number + 1;
		System.out.println(number);

	}

}
